import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { Snackbar } from "@mui/material";
import Alert from "@mui/material/Alert";
import Navbar from "../Navbar/Navbar";
import AddPassword from "./AddPassword"; // Assuming you have a similar component for listing passwords

const PasswordManager = () => {
  const navigate = useNavigate();

  const [alertMessage, setAlertMessage] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [passwordList, setPasswordList] = useState([]);
  const [user, setUser] = useState({
    website: "",
    username: "",
    password: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setPassword({ ...password, [name]: value });
  };

  const getPasswords = async () => {
    try {
      const token = localStorage.getItem("token");

      if (token) {
        const response = await axios.get(
          "http://localhost:4000/api/v1/getPasswords",
          {
            headers: {
              "x-access-token": token,
            },
          }
        );

        if (response.data.message === "Token Valid") {
          setPasswordList(response.data.UserData);
        }
      }
    } catch (error) {
      console.error("Error in Getting passwords:", error);
      toast.error("Error: " + (error.response?.data?.message || error.message), {
        theme: "dark",
      });
    }
  };

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      navigate("/login");
    } else {
      const user = jwtDecode(token);

      if (!user) {
        localStorage.removeItem("token");
        navigate("/login");
      } else {
        getPasswords();
      }
    }
  }, [navigate]);

  const addPassword = async () => {
    const { website, username, password: userPassword } = password;

    const token = localStorage.getItem("token");

    if (!token) {
      toast.error("Please Login to Add Passwords", { theme: "colored" });
      setTimeout(() => {
        navigate("/login");
      }, 2000);
      return;
    }

    if (!website || !username || !userPassword) {
      if (!website) {
        setAlertMessage("Website is required");
      } else if (!username) {
        setAlertMessage("Username is required");
      } else {
        setAlertMessage("Password is required");
      }

      setOpenSnackbar(true);

      setTimeout(() => {
        setOpenSnackbar(false);
        setAlertMessage(null);
      }, 3000);

      return;
    }

    if (userPassword.length < 6) {
      toast.error("Password must be at least 6 characters", {
        theme: "dark",
      });
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:4000/api/v1/addPassword",
        password,
        {
          headers: {
            "x-access-token": token,
          },
        }
      );

      if (response.data.message === "Password Added Successfully") {
        toast.success(response.data.message, { theme: "dark" });
        getPasswords();
        setPassword({ website: "", username: "", password: "" });
      }
    } catch (error) {
      toast.error("Error: " + (error.response?.data?.message || error.message), {
        theme: "colored",
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-200 to-purple-200 flex">
      <Navbar />

      <div className="flex-1 p-4 lg:ml-64">
        <ToastContainer autoClose={2000} style={{ marginTop: "20px" }} />

        <Snackbar
          open={openSnackbar}
          autoHideDuration={2000}
          onClose={() => setOpenSnackbar(false)}
          anchorOrigin={{ vertical: "top", horizontal: "right" }}
          style={{ marginTop: "10px" }}
        >
          <Alert variant="filled" severity="error">
            {alertMessage}
          </Alert>
        </Snackbar>

        <div className="max-w-4xl mx-auto bg-white shadow-lg rounded-lg overflow-hidden">
          <div className="p-4">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Add Password</h2>
            <div>
              <input
                className="w-full px-4 py-2 border border-gray-300 rounded mb-4 focus:outline-none focus:border-blue-500"
                type="text"
                name="website"
                value={password.website}
                placeholder="Website"
                onChange={handleChange}
                required
              />
              <input
                className="w-full px-4 py-2 border border-gray-300 rounded mb-4 focus:outline-none focus:border-blue-500"
                type="text"
                name="username"
                value={password.username}
                placeholder="Username"
                onChange={handleChange}
                required
              />
              <input
                className="w-full px-4 py-2 border border-gray-300 rounded mb-4 focus:outline-none focus:border-blue-500"
                type="password"
                name="password"
                value={password.password}
                placeholder="Password"
                onChange={handleChange}
                required
              />
            </div>
            <div className="flex justify-end">
              <button
                onClick={addPassword}
                className="bg-green-500 hover:bg-green-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline mr-4"
              >
                Add Password
              </button>
            </div>
          </div>
        </div>

        <AddPassword passwordList={passwordList} setPasswordList={setPasswordList} />
      </div>
    </div>
  );
};

export default PasswordManager;
