import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Sidebar from "../Navbar/"; // Adjust the import path based on your file structure

const Bgimage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
  };

  return (
    <div
      className="bg-image-container relative h-full w-full bg-cover bg-center"
      style={{
        backgroundImage:
          "url('https://cdn.dribbble.com/users/1341307/screenshots/5180233/landing-page_4x.png')",
      }}
    >
      <div className="overlay absolute inset-0 bg-black opacity-50"></div>
      <Slider {...settings} className="relative z-10">
        <div className="text-content flex justify-center items-center h-full text-center text-white text-3xl md:text-4xl lg:text-5xl xl:text-6xl">
          <h1 className="text-purple-500">Welcome to NoteVerse Platform</h1>
        </div>
        <div className="text-content flex justify-center items-center h-full text-center text-white text-3xl md:text-4xl lg:text-5xl xl:text-6xl">
          <h1>Write, Edit and Manage Notes at ease</h1>
        </div>
        <div className="text-content flex justify-center items-center h-full text-center text-white text-3xl md:text-4xl lg:text-5xl xl:text-6xl">
          <h1 className="text-blue-500">Keep your Notes Confidential and Secure</h1>
        </div>
      </Slider>
    </div>
  );
};

const Home = () => {
  return (
    <div className="home-container flex h-screen w-screen">
      <Sidebar />
      <div className="flex-grow">
        <Bgimage />
      </div>
    </div>
  );
};

export default Home;
