import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import jwtDecode from "jwt-decode";
import { ToastContainer, toast } from "react-toastify";
import { Snackbar } from "@mui/material";
import Alert from "@mui/material/Alert";
import Navbar from "../Navbar/Navbar";
import Addnote from "../AddNote/Addnote";

const Note = () => {
  const navigate = useNavigate();

  const [alertMessage, setAlertMessage] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);
  const [addlist, setAddlist] = useState([]);
  const [user, setUser] = useState({
    title: "",
    description: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser({ ...user, [name]: value });
  };

  const getNotes = async () => {
    try {
      const token = localStorage.getItem("token");

      if (token) {
        const response = await axios.get(
          "http://localhost:4000/api/v1/getNotes",
          {
            headers: {
              "x-access-token": token,
            },
          }
        );

        if (response.data.message === "Token Valid") {
          setAddlist(response.data.UserData);
        }
      }
    } catch (error) {
      console.error("Error in Getting notes:", error);
      toast.error("Error: " + error.response.data.message, {
        theme: "dark",
      });
    }
  };

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      navigate("/login");
    } else {
      const user = jwtDecode(token);

      if (!user) {
        localStorage.removeItem("token");
        navigate("/login");
      } else {
        getNotes();
      }
    }
  }, [navigate]);

  const addNotes = async () => {
    const { title, description } = user;

    const token = localStorage.getItem("token");

    if (!token) {
      toast.error("Please Login to Add Notes", { theme: "colored" });
      setTimeout(() => {
        navigate("/login");
      }, 2000);
      return;
    }

    if (!title || !description) {
      if (!title && description) {
        setAlertMessage("Title is required");
      } else if (title && !description) {
        setAlertMessage("Description is required");
      } else {
        setAlertMessage("Both title and description are required");
      }

      setOpenSnackbar(true);

      setTimeout(() => {
        setOpenSnackbar(false);
        setAlertMessage(null);
      }, 3000);

      return;
    }

    if (title.length < 3 || title.length > 25) {
      toast.error("Title must be between 3 and 25 characters", {
        theme: "dark",
      });
      return;
    }

    if (description.length <= 5) {
      toast.error("Description must be greater than 5 characters", {
        theme: "dark",
      });
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:4000/api/v1/addNotes",
        user,
        {
          headers: {
            "x-access-token": token,
          },
        }
      );

      if (response.data.message === "Notes Added Successfully") {
        toast.success(response.data.message, { theme: "dark" });
        getNotes();
        setUser({ title: "", description: "" });
      }
    } catch (error) {
      toast.error("Error: " + error.response.data.message, {
        theme: "colored",
      });
    }
  };

  const LogOut = () => {
    const token = localStorage.getItem("token");

    if (!token) {
      toast.error("Please Login First", { theme: "colored" });
      setTimeout(() => {
        navigate("/login");
      }, 2000);
      return;
    }

    localStorage.removeItem("token");
    toast.success("Logged out successfully", { theme: "colored" });
    setTimeout(() => {
      navigate("/login");
    }, 2000);
    return;
  };

  return (
    <div>
      <Navbar />

      <ToastContainer autoClose={2000} style={{ marginTop: "120px" }} />

      <Snackbar
        open={openSnackbar}
        autoHideDuration={2000}
        onClose={() => setOpenSnackbar(false)}
        anchorOrigin={{ vertical: "top", horizontal: "right" }}
        style={{ marginTop: "110px" }}
      >
        <Alert variant="filled" severity="error">
          {alertMessage}
        </Alert>
      </Snackbar>

      <div className="flex h-screen">
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 transition-opacity lg:hidden"></div>

        <div className="fixed inset-y-0 left-0 w-64 bg-gray-900 transition-transform transform lg:translate-x-0">
          <nav className="flex flex-col h-full">
            <div className="flex items-center justify-between px-4 py-3">
              <span className="text-xl font-semibold text-white">Menu</span>
              <button
                className="lg:hidden text-white focus:outline-none"
                onClick={() => {}}
              >
                ✕
              </button>
            </div>
            <ul className="flex flex-col flex-1">
              <li className="px-4 py-2">
                <Link
                  to="/"
                  className="text-white hover:text-blue-300"
                  onClick={() => setIsOpen(false)}
                >
                  Home
                </Link>
              </li>
              <li className="px-4 py-2">
                <Link
                  to="/login"
                  className="text-white hover:text-blue-300"
                  onClick={() => setIsOpen(false)}
                >
                  Login
                </Link>
              </li>
              <li className="px-4 py-2">
                <Link
                  to="/register"
                  className="text-white hover:text-blue-300"
                  onClick={() => setIsOpen(false)}
                >
                  Register
                </Link>
              </li>
              <li className="px-4 py-2">
                <Link
                  to="/note"
                  className="text-white hover:text-blue-300"
                  onClick={() => setIsOpen(false)}
                >
                  Add Notes
                </Link>
              </li>
            </ul>
          </nav>
        </div>

        <button
          className="fixed top-0 left-0 p-4 text-white lg:hidden focus:outline-none"
          onClick={() => {}}
        >
          ☰
        </button>

        <div className="flex-1 p-10">
          <div className="note-page">
            {console.log("ALL NOTES in addlist", addlist)}
            <div className="note-container">
              <div className="note-input">
                <input
                  className="note-inputval"
                  type="text"
                  name="title"
                  value={user.title}
                  placeholder="Title"
                  onChange={handleChange}
                  required
                />
                <textarea
                  className="note-inputval"
                  type="text"
                  name="description"
                  value={user.description}
                  onChange={handleChange}
                  rows={5}
                  placeholder="Add description..."
                  required
                />
              </div>
              <div className="note-btnflex">
                <button onClick={addNotes} className="note-btn">
                  Add Notes
                </button>
                <button onClick={LogOut} className="note-btn logout-button">
                  Log Out
                </button>
              </div>
            </div>
          </div>
          <Addnote addlist={addlist} setAddlist={setAddlist} />
        </div>
      </div>
    </div>
  );
};

export default Note;
