import React, { useState, useEffect } from "react";
import Sidebar from "../Navbar/Navbar"; // Assuming Sidebar.js is in the same directory

const Home = () => {
  const [currentImage, setCurrentImage] = useState(0);
  const images = [
    "https://cdn.dribbble.com/users/1341307/screenshots/5180233/landing-page_4x.png",
    "https://cdn.dribbble.com/users/1341307/screenshots/14627930/media/3866bc31c8588ce74421087fae3e237e.jpg?compress=1&resize=1200x900",
    ""];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImage((prevImage) =>
        prevImage === images.length - 1 ? 0 : prevImage + 1
      );
    }, 3000); // Change image every 3 seconds

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <div className="flex h-screen">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content */}
      <div className="flex-1 relative overflow-hidden">
        <img
          className="absolute inset-0 w-full h-full object-cover"
          src={images[currentImage]}
          alt="Background"
        />
        <div className="absolute inset-0 bg-black opacity-50"></div>
        <div className="absolute inset-0 flex items-center justify-center text-white text-center text-2xl md:text-4xl">
          <h1>Welcome to NoteVerse Platform</h1>
        </div>
      </div>
    </div>
  );
};

export default Home;
